example:
/* Bad */
.selector {
    margin: 0;padding:0;
}
/* Good */
.selector {
    margin: 0;
}

/* Bad */
div{
    margin:0;
}
/* Good */
div{
    margin: 0;
}

/* Bad */
article[character=juliet] {
    voice-family: "Vivien Leigh", victoria, female;
}
article[character='juliet'] {
    voice-family: "Vivien Leigh", victoria, female;
}
/* Good */
article[character="juliet"] {
    voice-family: "Vivien Leigh", victoria, female;
}

/* Bad */
div{
    margin:0;
    padding: 0;
}
/* Good */
div{
    margin :0;
}

/* Bad */
.success {
    background-color: #aaccaa;
}

/* Good */
.success {
    background-color: #aca;
}

/* Bad */
.success {
    background-color: #ACA;
    color: #90ee90;
}

/* Good */
.success {
    background-color: #aca;
    color: #90ee90;
}

/* Bad */
html[lang|=zh] q:before {
    font-family: 'Microsoft YaHei', sans-serif;
    content: '“';
}

/* Good */
html[lang|="zh"] q:before {
    font-family: "Microsoft YaHei", sans-serif;
    content: "“";
}

/* Bad */
.selector{
    font-family: Arial,sans-serif;
    font-family: Arial ,sans-serif;
}
/* Good */
.selector{
    font-family: Arial, sans-serif;
}

/* Bad */
.selector {
    margin: 0
}

/* Good */
.selector {
    margin: 0;
}

/* Bad */
.success {
    color: lightgreen;
}

/* Good */
.success {
    color: #90ee90;
}

/* Bad */
dialog#error,
p.danger-message {
    font-color: #c00;
}

/* Good */
#error,
.danger-message {
    font-color: #c00;
}

/* Bad */
body {
    background: url("bg.png");
}

/* Good */
body {
    background: url(bg.png);
}

/* Bad */
h1 {
    font-weight: bold;
}

/* Good */
h1 {
    font-weight: 700;
}

/* Bad */
.success {
    box-shadow: 0 0 2px rgba(0,128,0,.3);
    border-color: rgb(0, 128, 0);
}

/* Good */
.success {
    box-shadow: 0 0 2px rgba(0, 128, 0, .3);
    border-color: #008000;
}

/* Bad */
body {
    background-position: top; /* 50% 0% */
}

/* Good */
body {
    background-position: center top; /* 50% 0% */
}

/* Bad */
.selector{
    color: #000!important;
}

/* Good */
.selector{
    color: #000 !important;
}

/* Bad */
.container {
    line-height: initial;
}

/* Good */
.container {
    line-height: 1.5;
}

/* Bad */
@media (min-width: 20px),(min-height: 192px) {

}

/* Good */
@media
(-webkit-min-device-pixel-ratio: 2), /* Webkit-based browsers */
(min--moz-device-pixel-ratio: 2),    /* Older Firefox browsers (prior to Firefox 16) */
(min-resolution: 2dppx),             /* The standard way */
(min-resolution: 192dpi) {           /* dppx fallback */
    /* Retina-specific stuff here */
}

/* Bad */
.post, .page, .comment {
    line-height: 1.5;
}
/* Good */
.post,
.page,
.comment {
    line-height: 1.5;
}

/* Bad */
*{
    margin: 0;
}

/* Bad */
.selector{
    margin: 0 !important;
}

/* Bad */
body {
    padding: 0px 5px;
}

/* Good */
body {
    padding: 0 5px;
}

/* Bad */
panel {
    opacity: 0.8;
}

/* Good */
panel {
    opacity: .8;
}

/* Bad */
.box {
    transition: all 1s;
}

/* Good */
.box {
    transition: color 1s, border-color 1s;
}

/* Bad */
.selector{

}

/* Good */
.selector {

}

/* Bad */
main>nav {
    padding: 10px;
}

label+input {
    margin-left: 5px;
}

input:checked~button {
    background-color: #69C;
}
/* Good */
main > nav {
}

label + input {
}

input:checked ~ button {
}

/* Bad */
.page .header .login #username input {}
.comment div * {}

/* Good */
#username input {}
.comment .avatar {}

/* Bad */
.selector {
  margin: 0;
  padding: 0;
}
/* Good */
.selector {
    margin: 0;
    padding: 0;
}

/* Bad */
body {
    font-family: arial, sans-serif;
}
h1 {
    font-family: Arial, "Microsoft YaHei", sans-serif;
}
/* Good */
body {
    font-family: Arial, sans-serif;
}

h1 {
    font-family: Arial, "Microsoft YaHei", sans-serif;
}

/* Good */
.box {
    -webkit-box-sizing: border-box;
       -moz-box-sizing: border-box;
            box-sizing: border-box;
}
